mapper.update:
update doctocinfotable
set modifyinfo = #{flag}
where doctorId = #{doctorId};

update doctocinfotable
set refuseAppointment = #{flag}
where doctorId = #{doctorId};

update patientnum
set patientNum = #{num}
where doctorId = #{doctorId};

update recordtable
set flag = #{flag}
where recordIdx = #{recordIdx};

mapper.select:
select * from recordtable where timeId = #{timeId}
                            and doctorId = #{doctorId}
                            and date = #{date}
                            and flag = #{status};
